        fdict = {}
        for key, value in methods.items():
            fdict[key] = u'#-' + key
            fdict[value] = fdict[key]

        server_name = self.escape(server_name)
        head = u'<big><big><strong>%s</strong></big></big>' % server_name
        result = self.heading(head, u'#ffffff', u'#7799ee')
        doc = self.markup(package_documentation, self.preformat, fdict)
        doc = doc and u'<tt>%s</tt>' % doc
        result = result + u'<p>%s</p>\n' % doc
        contents = []
        method_items = sorted(methods.items())
        for key, value in method_items:
            contents.append(self.docroutine(value, key, funcs=fdict))

        result = result + self.bigsection(u'Methods', u'#ffffff', u'#eeaa77', u''.join(contents))
        return result


